import React from "react";

import {
  Button,
  Card,
  Col,
  Container,
  ListGroup,
  Row,
  Table,
} from "react-bootstrap";
import Form from "react-bootstrap/Form";
// css
import "../styles/common.scss";

export default class Dashboard extends React.Component {
  state = {
    numOnboarding: 1,
  };

  componentDidMount() {
    let addEmployee = false;
    if (this.props?.location?.state) {
      addEmployee = this.props.location.state.addEmployee;
    }
    if (addEmployee) {
      this.setState((state) => {
        // Important: read `state` instead of `this.state` when updating.
        return { numOnboarding: state.numOnboarding + 1 };
      });
    }
  }

  render() {
    let numOnboarding = this.state.numOnboarding;
    let numHired = 2;
    let numTotal = numOnboarding + numHired;
    return (
      <div class="pageWithNav dashboard">
        <Container>
          <Row className="top20">
            <Col>
              <SummaryCard numTotal={numTotal} />
            </Col>
            <Col>
              <ExpensesCard />
            </Col>
          </Row>

          <Row className="top20">
            <Col>
              <DetailsCard />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

function SummaryCard(props) {
  return (
    <Card className="full-height">
      <Card.Body>
        <Card.Title>
          Summary

          <div className="dropdown" className="float-right">
            <Form.Group>
              <Form.Control as="select">
                <option default>Feb 2021</option>
                <option>Jan 2021</option>
                <option>Dec 2020</option>
                <option>Nov 2020</option>
              </Form.Control>
            </Form.Group>
          </div>

        </Card.Title>

        <Table hover class="mb-5">
          <thead>
            <tr>
              <th>February 2021 Total </th>
              <th>110,468</th>
              <th>100%</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Salary</td>
              <td>72,473</td>
              <td>66%</td>
            </tr>
            <tr>
              <td>Taxes</td>
              <td>21,296</td>
              <td>19%</td>
            </tr>
            <tr>
              <td>Pension</td>
              <td>2,655</td>
              <td>2%</td>
            </tr>
            <tr>
              <td>Employment Insurance</td>
              <td>4,238</td>
              <td>4%</td>
            </tr>
            <tr>
              <td>Private Healthcare</td>
              <td>6,172</td>
              <td>6%</td>
            </tr>
            <tr>
              <td>Travel Allowance</td>
              <td>3,634</td>
              <td>3%</td>
            </tr>
          </tbody>
        </Table>
      </Card.Body>
    </Card>
  );
}

